const delay=5e3;let running=!1;const request=new XMLHttpRequest;request.open("GET","http://www.higherlowergame.com/questions/get/general",!1),request.setRequestHeader("User-Agent","Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/99.0.4844.82 Safari/537.36"),request.send();const data=JSON.parse(request.responseText),answers=new Map;for(let i=0;i<data.length;i++)answers.set(data[i].keyword.toUpperCase(),data[i].searchVolume);function start(){running=!0,setTimeout(()=>{if(null!=data){let e=setInterval(()=>{if(0!==document.getElementsByClassName("term-volume__volume").length){let t=document.getElementsByClassName("term-keyword__keyword")[0].innerText,s=answers.get(t.substring(1,t.length-1).toUpperCase()),n=document.getElementsByClassName("term-keyword__keyword")[1].innerText;answers.get(n.substring(1,n.length-1).toUpperCase())>s?document.getElementsByClassName("term-actions__button--higher")[0].click():document.getElementsByClassName("term-actions__button--lower")[0].click()}else clearInterval(e),running=!1},5e3)}else running=!1},2e3)}setInterval(()=>{running||0===document.getElementsByClassName("term-volume__volume").length||start()},500);
